import React, { useState } from 'react';
import './App.css';
import axios from 'axios';

const Create = () => {
    const [task, setTask] = useState('');

    const createTask = () => {
        axios.post('http://localhost:5000/add', { task: task.trim() })
            .then(result => {
                console.log(result.data);
                window.location.reload();
                setTask('');
            })
            .catch(err => console.log(err));
    };

    return (
        <main>
            <h3>where those two lists rarely meet!</h3>
            <h3>where those two lists rarely meet!</h3>
            <h3>Life is short. Make a list of things you love to do, then make a list of things you actually have to do.  </h3>
            <h1 className='todo-title'>WELCOME TO YOUR TO-DO LIST</h1>
            
            <div className='create-form'>
                <input
                    type='text'
                    placeholder='Enter a task'
                    value={task}
                    onChange={(e) => setTask(e.target.value)}
                    required
                />
                <button onClick={createTask}>ADD</button>
            </div>
        </main>
    );
};

export default Create;
